import { useDispatch, useSelector } from "react-redux";
import { increment, decrement } from "./store/counter";
import modal, { open, close } from "./store/modal";

function App() {
  /* useSelector -> utilizado para ter acesso ao estado do Redux globalmente */
  const state = useSelector((state) => state.total);
  const dispatch = useDispatch();
  return (
    <div className="App">
      {modal && <h1>Total: {state}</h1>}
      <button onClick={() => dispatch(increment())}>Incrementar</button>
      <button onClick={() => dispatch(decrement())}>Decrementar</button>
      <button onClick={() => dispatch(open())}>Abrir</button>
      <button onClick={() => dispatch(close())}>Fechar</button>
    </div>
  );
}

export default App;
