import { createSlice } from "@reduxjs/toolkit";

/* primeira forma de utilizar */
const slice = createSlice({
  name: "counter",
  initialState: {
    total: 0,
  },
  reducers: {
    increment(state) {
      state.total++;
    },
    decrement(state) {
      state.total--;
    },
  },
});

export const { increment, decrement } = slice.actions;
export default slice.reducer;
